import { useState, useRef } from 'react';
import firebase from "../../firebase_config/firebaseConfig";
import classes from './AuthForm.module.css';
import SocialLogin from './SocialLogin';

const AuthForm = () => {

  const handleClick = (event) => {
      event.preventDefault();
      let recaptcha = new firebase.auth.RecaptchaVerifier("recaptcha-container");
      let number = "7978914566";
      firebase
        .auth()
        .signInWithPhoneNumber(number, recaptcha)
        .then((e) => {
          let code = prompt("enter otp");
          if (code == null) {
            return;
          }
          e.confirm(code).then((res) => {
            console.log(res, "................");
          });
        })
        .catch((e) => {
          console.log(e);
        });
     }
  return (
    <section className={classes.auth}>
      <h1>Login</h1>
      <form onSubmit = {handleClick}>
        <div className={classes.control}>
          <label htmlFor='mobileNo'>Your Mobile Number</label>
          <input type='number' id='mobileNo' minLength = '10' maxLength = '10' required/>
        </div>
        <div id="recaptcha-container"></div>

        <div className={classes.actions}>
          <button>Get OTP</button>
          <SocialLogin />
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
