import { useEffect } from 'react';
import { Switch, Route } from 'react-router-dom';
import { retrieveStoredToken } from "./store/auth";
import { useDispatch, useSelector } from "react-redux";
import { authAction } from "./store/auth";
import Layout from './components/Layout/Layout';
import UserProfile from './components/Profile/UserProfile';
import AuthPage from './pages/AuthPage';
import HomePage from './pages/HomePage';

function App() {
  let logoutTimer;
  let tokenData;
  const logout = useDispatch(authAction.logout());
  tokenData = useSelector(state => state.token);
  if(tokenData){
  tokenData = retrieveStoredToken();
  }
  useEffect(() => {
    debugger;
    if (tokenData) {
      console.log(tokenData.duration);
      logoutTimer = setTimeout(logout,tokenData.duration);
    }
    return () => {
      console.log("CLEANUP");
      clearTimeout(logoutTimer);
    };
  }, [tokenData]);

  return (
    <Layout>
      <Switch>
        <Route path='/' exact>
          <HomePage />
        </Route>
        <Route path='/auth'>
          <AuthPage />
        </Route>
        <Route path='/profile'>
          <UserProfile />
        </Route>
      </Switch>
    </Layout>
  );
}

export default App;
