import React from 'react'
import { facebookProvider } from '../../firebase_config/authMethods';
import SocialMediaAuth from '../../service/SocialMediaAuth';

function SocialLogin() {
    const loginHandler = async provider => {
        const res = await SocialMediaAuth(provider);
        console.log(res);
    }
    
    return (
        <div>
            <button type = 'button' onClick = {() => loginHandler(facebookProvider)}>Facebook</button>
            <button type = 'button'>Google</button>
        </div>
    )
}

export default SocialLogin;
